import { useEffect } from "react"

function Filter({ popular, setFiltered, setActiveGenre, activeGenre }) {
  useEffect(() => {
    if (activeGenre === 0) {
      setFiltered(popular)
      return
    }
  }, [activeGenre])

  return (
    <div className="filter-container">
      <button
        onClick={() => {
          setActiveGenre(0)
        }}
      >
        All
      </button>

      <button
        onClick={() => {
          setActiveGenre(35)
        }}
      >
        Comedy
      </button>

      <button
        onClick={() => {
          setActiveGenre(28)
        }}
      >
        Action
      </button>
    </div>
  )
}

export default Filter
